import React from 'react'
import ReactDOM from 'react-dom'

const body = document.querySelector('body')
body.style.background = `rgb(155,155,210)`

const date = new Date()
// const date = new Date(2022,5,10,15,0,0)
const hour = date.getHours()
const cssStyle = {}
var text = ''
if(hour >= 0 && hour <= 11){
  text = 'Morning'
  cssStyle.color = 'yellow'
}
else if(hour > 11 && hour < 19){
  text = 'Afternoon'
  cssStyle.color = 'crimson'
}
else{
  text = 'Night'
  cssStyle.color = 'white'

}

var x = 100 +  'px';


const center = {
  display: 'flex',
  justifyContent: 'center',
  background: 'rgb(90,255,120)',
  fontFamily: 'Sriracha',
  marginTop: x,
  marginRight: '15%',
  marginLeft: '15%',
  borderRadius: '8%'
}
const orange = {
  color: 'orange',
  marginRight: '20px'
}

ReactDOM.render(
  <>
  <div style={center}><h1 style = {orange}>Hello Sir,</h1>&nbsp;<h1 style = {cssStyle}>Good {text}</h1></div>
  </>,
  document.getElementById('root')
)
