import React from 'react'
import ReactDOM from 'react-dom'

//Internal Styling using Object Notation
var decoration1 = {fontFamily: "sans-serif", color : "rgb(120,120,120)", fontSize: "25px"}




ReactDOM.render(
  //In React v16 it's possible for render() to return multiple element
  [<h1 style = {decoration1}>Hello React1</h1>,
   <h3> Hello React </h3>
  ],
  document.getElementById('root')
);





//Adding New Elements Using Javascript 

var h1 = document.createElement('h1')
h1.innerHTML = "This Tag is created using Javascript"
h1.style.fontFamily = "sans-serif"
document.getElementById('root').appendChild(h1)
