import React from 'react'
import ReactDOM from 'react-dom'

//Internal Styling using Object Notation
var decoration1 = {fontFamily: "sans-serif", color : "rgb(120,120,120)", fontSize: "25px"}



//In React v16 it's possible for render()
// to return multiple element

//React.Fragment is used because it is faster than array
// and uses less memory than div
ReactDOM.render(

  <React.Fragment>
    <h1 style = {decoration1}>Hello React1</h1>
    <h2> Hello React </h2>
  </React.Fragment>,

  document.getElementById('root')
);





//Adding New Elements Using Javascript 

var h3 = document.createElement('h3')
h3.innerHTML = "This Tag is created using Javascript"
h3.style.fontFamily = "sans-serif"
document.getElementById('root').appendChild(h3)
