import React from 'react'
import ReactDOM from 'react-dom'

const body = document.querySelector('body')
body.style.background = `rgb(155,155,210)`

const date = new Date(2020,10,26,20,0,0,0)
const hour = date.getHours();
var text = ''
if(hour >= 0 && hour <= 11)
  text = 'Morning'
else if(hour > 11 && hour <= 19)
  text = 'Afternoon'
else
  text = 'Night'

var x = 100 +  'px';


