  display: 'flex',
  justifyContent: 'center',
  background: 'rgb(90,255,120)',
  fontFamily: 'Sriracha',
  marginTop: x
}
const orange = {
  color: 'orange',
  marginRight: '20px'
}
const green = {
  color: 'green'
}
